:root{--base-color:#ff0100;--color-dark:#313131;--color-light:#6e6e6e;--page-max-width:850px}body,html{margin:0;padding:0}a:active,a:hover{outline:0}b,strong{font-weight:700}img{border:0}button,input,optgroup,select,textarea{color:inherit;font:inherit;margin:0}button,select{text-transform:none}textarea{overflow:auto}*,::after,::before{-webkit-box-sizing:border-box;box-sizing:border-box}body{font-weight:400;line-height:1;text-rendering:optimizeLegibility;word-wrap:break-word;-webkit-overflow-scrolling:touch;-webkit-text-size-adjust:none}body,button,input{-moz-osx-font-smoothing:grayscale;-webkit-font-smoothing:antialiased}img,video{max-width:100%;height:auto}blockquote,dd,div,dl,dt,form,h1,h2,h3,h4,h5,h6,li,ol,p,pre,td,th,ul{margin:0;padding:0}h1,h2,h3,h4,h5,h6{-webkit-font-variant-ligatures:common-ligatures;font-variant-ligatures:common-ligatures;text-rendering:optimizeLegibility}em,i{font-style:italic;line-height:inherit}small{font-size:60%;line-height:inherit}ol,ul{list-style:none}li{display:block}a{text-decoration:none;line-height:inherit}body,html{height:100%}body{background-color:#fff;font-family:serif;font-size:1rem;font-weight:1.5;line-height:1.4;color:var(--color-light)}.intro{height:100%;display:-webkit-box;display:-ms-flexbox;display:flex;text-align:center;-webkit-box-align:center;-ms-flex-align:center;align-items:center;-webkit-box-pack:center;-ms-flex-pack:center;justify-content:center;background-size:cover;background-position:50%;background-repeat:no-repeat;background-color:#111;-webkit-transition:background-color .5s ease-in;-o-transition:background-color .5s ease-in;transition:background-color .5s ease-in}.button{text-decoration:none;text-align:center;white-space:nowrap;font-family:sans-serif;display:inline-block;letter-spacing:.2rem;font-weight:700;border-radius:44px;text-transform:uppercase;font-size:.85rem;border:3px solid transparent;padding:1rem 2rem}.intro__content{position:relative}.intro__content h1{color:#fff;line-height:1.071;max-width:900px;margin-top:0;margin-bottom:.6rem;margin-left:auto;margin-right:auto;text-shadow:0 0 20px rgba(0,0,0,.5);font-family:sans-serif}@media (min-width:601px){.intro__content h1{font-size:5rem}}@media (min-width:0) and (max-width:600px){.intro__content h1{font-size:3.5rem}}@media (orientation:landscape){.isPhone .intro__content h1{font-size:4rem}}.intro__content .button{color:#fff;border-color:rgba(255,255,255,.3)}@media (min-width:0) and (max-width:480px){.intro__content .button{padding:.7rem 1.5rem;letter-spacing:.1rem}}.btn__group{display:-webkit-box;display:-ms-flexbox;display:flex;-webkit-box-pack:center;-ms-flex-pack:center;justify-content:center;margin-top:.6rem}.btn__group li+li{margin-left:-3px;position:relative}.btn__group li:nth-last-child(2):first-child .button,.btn__group li:nth-last-child(2)~li:first-child .button{border-top-right-radius:0;border-bottom-right-radius:0}.btn__group li:nth-last-child(2):last-child .button,.btn__group li:nth-last-child(2)~li:last-child .button{border-top-left-radius:0;border-bottom-left-radius:0}.intro__cta li+li .button{border-left-color:transparent}.intro__cta li+li .button:hover{border-left-color:var(--base-color)}.intro__msg{font-family:sans-serif;color:var(--base-color);font-size:1.3rem;margin-bottom:0;font-weight:700;text-transform:uppercase;letter-spacing:.2rem;text-shadow:0 0 6px rgba(0,0,0,.2)}.intro__position{text-transform:uppercase;letter-spacing:.1rem;color:#fff;display:-webkit-box;display:-ms-flexbox;display:flex;-webkit-box-pack:center;-ms-flex-pack:center;justify-content:center;text-shadow:0 0 6px rgba(0,0,0,.2);margin:0 1rem 4em}.intro__position span+span:before{content:"\7c";text-align:center;display:inline-block;color:rgba(255,255,255,.3)}@media (min-width:601px){.intro__position{font-size:1.2rem}.intro__position span+span:before{padding:0 1.3rem}}@media (min-width:0) and (max-width:600px){.intro__position{font-size:.9rem;line-height:2rem;-ms-flex-wrap:wrap;flex-wrap:wrap}.intro__position span+span:before{padding:0 .9rem}}@media (orientation:landscape){.isPhone .intro__position{margin-bottom:1rem}}.intro__social{display:-webkit-box;display:-ms-flexbox;display:flex;position:absolute;width:100%;left:0;bottom:4rem;margin:0;padding:0;-webkit-box-pack:center;-ms-flex-pack:center;justify-content:center}.intro__social li{display:inline-block;margin:0 1rem;padding:0}.intro__social a{color:rgba(255,255,255,.8)}@media (orientation:landscape){.isPhone .intro__social{bottom:1rem}}